  --shadow-sm: 0px 2px 8px 0px hsl(217 91% 60% / 0.1), 0px 1px 2px -1px hsl(217 91% 60% / 0.05);
  --shadow: 0px 4px 12px 0px hsl(217 91% 60% / 0.15), 0px 2px 4px -1px hsl(217 91% 60% / 0.08);
  --shadow-md: 0px 6px 16px 0px hsl(217 91% 60% / 0.18), 0px 3px 6px -1px hsl(217 91% 60% / 0.1);
  --shadow-lg: 0px 10px 25px 0px hsl(217 91% 60% / 0.2), 0px 5px 10px -1px hsl(217 91% 60% / 0.12);
  --shadow-xl: 0px 15px 35px 0px hsl(217 91% 60% / 0.25), 0px 8px 15px -1px hsl(217 91% 60% / 0.15);
  --shadow-2xl: 0px 25px 50px 0px hsl(217 91% 60% / 0.3);

  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
