@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }

  html {
    scroll-behavior: smooth;
  }
}

@layer utilities {
  input[type="search"]::-webkit-search-cancel-button {
    @apply hidden;
  }

  [contenteditable][data-placeholder]:empty::before {
    content: attr(data-placeholder);
    color: hsl(var(--muted-foreground));
    pointer-events: none;
  }

  .no-default-hover-elevate {}
  .no-default-active-elevate {}

  .toggle-elevate::before,
  .toggle-elevate-2::before {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    border-radius: inherit;
    z-index: -1;
  }

  .toggle-elevate.toggle-elevated::before {
    background-color: var(--elevate-2);
  }

  .border.toggle-elevate::before {
    inset: -1px;
  }

  .hover-elevate:not(.no-default-hover-elevate),
  .active-elevate:not(.no-default-active-elevate),
  .hover-elevate-2:not(.no-default-hover-elevate),
  .active-elevate-2:not(.no-default-active-elevate) {
    position: relative;
    z-index: 0;
  }

  .hover-elevate:not(.no-default-hover-elevate)::after,
  .active-elevate:not(.no-default-active-elevate)::after,
  .hover-elevate-2:not(.no-default-hover-elevate)::after,
  .active-elevate-2:not(.no-default-active-elevate)::after {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    border-radius: inherit;
    z-index: 999;
  }

  .hover-elevate:hover:not(.no-default-hover-elevate)::after,
  .active-elevate:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-1);
  }

  .hover-elevate-2:hover:not(.no-default-hover-elevate)::after,
  .active-elevate-2:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-2);
  }

  .border.hover-elevate:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate-2:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate-2:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after {
    inset: -1px;
  }

  .glassmorphism {
    @apply backdrop-blur-xl bg-background/30 border border-white/10;
  }

  .glassmorphism-card {
    @apply backdrop-blur-xl bg-card/50 border border-primary/20;
  }

  .neon-glow {
    box-shadow: 
      0 0 5px hsl(var(--primary)),
      0 0 10px hsl(var(--primary) / 0.5),
      0 0 20px hsl(var(--primary) / 0.3),
      0 0 40px hsl(var(--primary) / 0.1);
  }

  .neon-glow-sm {
    box-shadow: 
      0 0 3px hsl(var(--primary)),
      0 0 6px hsl(var(--primary) / 0.4),
      0 0 12px hsl(var(--primary) / 0.2);
  }

  .neon-glow-cyan {
    box-shadow: 
      0 0 5px hsl(var(--neon-cyan)),
      0 0 10px hsl(var(--neon-cyan) / 0.5),
      0 0 20px hsl(var(--neon-cyan) / 0.3);
  }

  .neon-glow-whatsapp {
    box-shadow: 
      0 0 5px hsl(var(--whatsapp-green)),
      0 0 10px hsl(var(--whatsapp-green) / 0.5),
      0 0 20px hsl(var(--whatsapp-green) / 0.3),
      0 0 40px hsl(var(--whatsapp-green) / 0.1);
  }

  .neon-text {
    text-shadow: 
      0 0 5px hsl(var(--primary)),
      0 0 10px hsl(var(--primary) / 0.5),
      0 0 20px hsl(var(--primary) / 0.3);
  }

  .neon-text-cyan {
    text-shadow: 
      0 0 5px hsl(var(--neon-cyan)),
      0 0 10px hsl(var(--neon-cyan) / 0.5);
  }

  .gradient-text {
    @apply bg-gradient-to-r from-primary via-blue-400 to-cyan-400 bg-clip-text text-transparent;
  }

  .gradient-border {
    position: relative;
  }

  .gradient-border::before {
    content: "";
    position: absolute;
    inset: 0;
    padding: 1px;
    border-radius: inherit;
    background: linear-gradient(135deg, hsl(var(--primary)), hsl(var(--neon-cyan)), hsl(var(--neon-purple)));
    -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    -webkit-mask-composite: xor;
    mask-composite: exclude;
  }

  .animated-gradient-bg {
    background: linear-gradient(
      -45deg,
      hsl(var(--background)),
      hsl(220 20% 8%),
      hsl(217 30% 10%),
      hsl(220 20% 6%)
    );
    background-size: 400% 400%;
    animation: gradient-shift 15s ease infinite;
  }

  @keyframes gradient-shift {
    0% { background-position: 0% 50%; }
    50% { background-position: 100% 50%; }
    100% { background-position: 0% 50%; }
  }

  @keyframes float {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-10px); }
  }

  @keyframes pulse-glow {
    0%, 100% { 
      box-shadow: 0 0 5px hsl(var(--primary)), 0 0 10px hsl(var(--primary) / 0.5);
    }
    50% { 
      box-shadow: 0 0 10px hsl(var(--primary)), 0 0 20px hsl(var(--primary) / 0.7), 0 0 30px hsl(var(--primary) / 0.4);
    }
  }

  @keyframes shimmer {
    0% { background-position: -200% 0; }
    100% { background-position: 200% 0; }
  }

  .animate-float {
    animation: float 3s ease-in-out infinite;
  }

  .animate-pulse-glow {
    animation: pulse-glow 2s ease-in-out infinite;
  }

  .animate-shimmer {
    background: linear-gradient(
      90deg,
      transparent,
      hsl(var(--primary) / 0.1),
      transparent
    );
    background-size: 200% 100%;
    animation: shimmer 2s infinite;
  }

  .glowing-input {
    @apply transition-all duration-300;
  }

  .glowing-input:focus {
    box-shadow: 
      0 0 0 2px hsl(var(--background)),
      0 0 0 4px hsl(var(--primary) / 0.3),
      0 0 20px hsl(var(--primary) / 0.2);
  }

  .card-futuristic {
    @apply relative overflow-hidden rounded-xl border border-primary/20 bg-card/80 backdrop-blur-sm transition-all duration-300;
  }

  .card-futuristic::before {
    content: "";
    position: absolute;
    inset: 0;
    background: linear-gradient(135deg, hsl(var(--primary) / 0.1), transparent 50%);
    pointer-events: none;
  }

  .card-futuristic:hover {
    border-color: hsl(var(--primary) / 0.5);
    box-shadow: 
      0 0 20px hsl(var(--primary) / 0.15),
      0 10px 40px hsl(var(--primary) / 0.1);
  }

  .skill-badge {
    @apply inline-flex items-center gap-1.5 px-4 py-2 rounded-full text-sm font-medium transition-all duration-300;
    background: linear-gradient(135deg, hsl(var(--primary) / 0.15), hsl(var(--neon-cyan) / 0.1));
    border: 1px solid hsl(var(--primary) / 0.3);
  }

  .skill-badge:hover {
    border-color: hsl(var(--primary) / 0.6);
    box-shadow: 0 0 15px hsl(var(--primary) / 0.3);
    transform: translateY(-2px);
  }

  .section-divider {
    @apply w-full h-px;
    background: linear-gradient(90deg, transparent, hsl(var(--primary) / 0.5), transparent);
  }
}

